import React, { Component } from 'react';

const ProgressComp = (props) => {

    function selectBar(e) {
        let idProgress = e.target.getAttribute("id");
        const progressEl = document.getElementsByClassName("progressBar");

        for (let i = 0; i < progressEl.length; i++) {
            progressEl[i].classList.remove("selected");
        }
        console.log(e.target);
        //document.getElementById(idProgress).classList.add("selected");
    }

    return (
        <div id={props.id} className={props.class} onClick={selectBar}>
            <div value={props.value} className="percent">{props.num}</div>
            <div style={{ width: props.num }} className="back"></div>
        </div>
    );
}

const Button = (props) => {

    function changeBar(e) {

        let number = document.querySelector(".progressBar.selected .percent");
        let back = document.querySelector(".progressBar.selected .back");

        const limit = props.limit;
        let initialVal = number.getAttribute("value");
        let val = e.target.value;


        if (val.indexOf('-') === -1) {

            console.log('Positive!');

            let initPercent = Math.round(initialVal * 100 / limit);
            let valPercent = Math.round(val * 100 / limit);
            let calcPercent = initPercent + valPercent;
            let calc = parseInt(initialVal) + parseInt(val);

            back.classList.remove("error");

            if (calcPercent > 100) {
                back.style.width = "100%";
                back.classList.add("error");
            } else {
                back.style.width = calcPercent + "%";
            }

            number.setAttribute("value", calc);
            number.innerHTML = calcPercent + "%";

        } else {

            console.log('Negative!');

            let valPos = val * -1;

            let initPercent = Math.round(initialVal * 100 / limit);
            let valPercent = Math.round(valPos * 100 / limit);
            let calcPercent = initPercent - valPercent;
            let calc = parseInt(initialVal) - parseInt(valPos);

            if (calcPercent < 0) {
                back.style.width = "0%";
                number.setAttribute("value", 0);
                number.innerHTML = "0%";
            } else {
                back.style.width = calcPercent + "%";
                number.setAttribute("value", calc);
                number.innerHTML = calcPercent + "%";

                if (calcPercent < 100) {
                    back.classList.remove("error");
                }
            }
        }

        console.log("Limit: " + limit, "ValueBar: " + number.getAttribute("value"));

    }

    console.log(props.value);

    return (
        <button value={props.value} onClick={changeBar}>
            {(props.value < 0) ? props.value : "+" + props.value}
        </button>
    );
}

const SelectBox = (props) => {

    function selectBar(e) {
        let idProgress = e.target.value;
        const progressEl = document.getElementsByClassName("progressBar");
        console.log(idProgress);

        for (let i = 0; i < progressEl.length; i++) {
            progressEl[i].classList.remove("selected");
        }
        document.getElementById(idProgress).classList.add("selected");
    }

    return (
        <select onChange={selectBar}>
            {
                props.value.map((value, index) =>
                    <option key={index} value={'progress' + index}>Progress {index + 1}</option>
                )
            }
        </select>
    );
}

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            progData: []
        };
    }

    componentDidMount() {

        //- API call data get
        fetch('https://pb-api.herokuapp.com/bars')
            .then(response => { return response.json(); })
            .then((data) => { this.setState({ progData: data, loading: false }) })
            .catch((error) => { this.setState({ error }) });

    }

    render() {

        const { progData, loading, error } = this.state;

        if (error) {
            return <main><span>Something went wrong...</span></main>;
        } else if (loading) {
            return <main><h2>Please wait...</h2></main>;
        } else {

            // Log correctly
            console.log(
                "Limit: " + progData.limit,
                "Progress bars: " + progData.bars,
                "Buttons: " + progData.buttons);

            return (
                <main>
                    <section id="progressBox">
                        <span>Choose your progress bar:</span>
                        <div className="choose">
                            {progData && progData.bars && (
                                progData.bars.map((value, index) =>
                                    <ProgressComp
                                        class={index === 0 ? "progressBar selected" : "progressBar"}
                                        key={index}
                                        id={'progress' + index}
                                        value={value}
                                        num={Math.round(value * 100 / progData.limit) + '%'}
                                    />
                                )
                            )}
                        </div>
                    </section>

                    <section id="controls">

                        {progData && progData.bars && (
                            <SelectBox value={progData.bars} />
                        )}

                        <div className="change">
                            <span>Change your progress bar:</span>
                            {progData && progData.buttons && (
                                progData.buttons.map((value, index) =>
                                    <Button key={index} limit={progData.limit} value={value} />
                                )
                            )}
                        </div>

                    </section>
                </main>
            );
        }
    }
}

export default Main;
