import React from 'react';

function ProgressComp(props) {
    return (
        <div id={props.id} className="progressBar">
            <div className="percent">0%</div>
            <div className="back"></div>
        </div>
    );
}

function Button(props) {

    function changeBar() {
        let progDiv = document.getElementById(props.progress);
        let number = progDiv.getElementsByClassName("percent")[0];
        number.innerHTML = props.value;
        console.log(number);
    }

    return (
        <button onClick={changeBar}>
            {props.value}
        </button>
    );
}

function Main() {

    return (
        <main>
            <section id="progress">
                <ProgressComp id="first" />
                <ProgressComp id="second" />
                <ProgressComp id="third" />
            </section>
            <section id="controls">
                <div className="choose">
                    <span>Choose your progress bar:</span>
                    <select>
                        <option value="first">Progress 1</option>
                        <option value="second">Progress 2</option>
                        <option value="third">Progress 3</option>
                    </select>
                </div>
                <div className="change">
                    <span>Change your progress bar:</span>
                    <Button progress="first" value="-25" />
                    <Button progress="first" value="-10" />
                    <Button progress="first" value="+10" />
                    <Button progress="first" value="+25" />
                </div>
            </section>
        </main>
    );
}

export default Main;
