import React, { Component } from 'react';

class ProgressComp extends React.Component {
    render() {
        return (
            <div id={this.props.id} className={this.props.class}>
                <div value={this.props.value} className="percent">{this.props.num}</div>
                <div style={{ width: this.props.num }} className="back"></div>
            </div>
        );
    }
}

class Button extends React.Component {

    changeBar(e) {

        let number = document.querySelector(".progressBar.selected .percent");
        let back = document.querySelector(".progressBar.selected .back");

        let initialVal = number.getAttribute("value");
        let val = e.target.value;

        if (val.indexOf('-') === -1) {

            console.log('Positive!');

            back.classList.remove("error");
            let calc = parseFloat(val) + parseFloat(initialVal);

            if (calc > 100) {
                back.style.width = "100%";
                back.classList.add("error");
            } else {
                back.style.width = calc + "%";
            }

            number.setAttribute("value", calc);
            number.innerHTML = calc + "%";

        } else {

            console.log('Negative!');
            console.log(Math.floor(Number(val)));

            let calc = parseFloat(initialVal) - Math.floor(parseFloat(val));

            if (calc < 0) {
                back.style.width = "0%";
                number.setAttribute("value", 0);
                number.innerHTML = "0%";
            } else {
                back.style.width = calc + "%";
                number.setAttribute("value", calc);
                number.innerHTML = calc + "%";
            }
        }

        console.log(initialVal);
        console.log(val);
    }

    render() {

        return (
            <button value={this.props.value} onClick={this.changeBar}>
                {this.props.value}
            </button>
        );
    }
}

class SelectBox extends React.Component {

    selectBar(e) {
        let idProgress = e.target.value;
        const progressEl = document.getElementsByClassName("progressBar");
        console.log(idProgress);

        for (let i = 0; i < progressEl.length; i++) {
            progressEl[i].classList.remove("selected");
        }
        document.getElementById(idProgress).classList.add("selected");
    }

    render() {
        return (
            <select onChange={this.selectBar}>
                {
                    this.props.value.map((value, index) =>
                        <option key={index} value={'progress' + index}>Progress {index + 1}</option>
                    )
                }
            </select>
        );
    }
}

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            progData: []
        };
    }

    componentDidMount() {
        fetch('http://pb-api.herokuapp.com/bars')
            .then(response => { return response.json(); })
            .then((data) => { this.setState({ progData: data, loading: false }) })
            .catch((error) => { this.setState({ error }) });
    }

    render() {

        const { progData, loading, error } = this.state;

        if (error) {
            return <main><span>Something went wrong...</span></main>;
        } else if (loading) {
            return <main><h2>Please wait...</h2></main>;
        } else {
            // Log correctly
            console.log(progData.bars);
            return (
                <main>
                    <section id="progressBox">
                        {progData && progData.bars && (
                            progData.bars.map((value, index) =>
                                <ProgressComp class={index === 0 ? "progressBar selected" : "progressBar"} key={index} id={'progress' + index} value={value} num={value + '%'} />
                            )
                        )}
                    </section>

                    <section id="controls">

                        <div className="choose">
                            <span>Choose your progress bar:</span>
                            {progData && progData.bars && (
                                <SelectBox value={progData.bars} />
                            )}
                        </div>

                        <div className="change">
                            <span>Change your progress bar:</span>
                            {progData && progData.buttons && (
                                progData.buttons.map((value, index) =>
                                    <Button key={index} value={value} />
                                )
                            )}
                        </div>

                    </section>
                </main>
            );
        }
    }
}

export default Main;
