import React, { Component } from 'react';

function ProgressComp(props) {
    console.log(props);
    return (
        <div id={props.id} className="progressBar">
            <div className="percent">{props.value}</div>
            <div style={{ width: props.value }} className="back"></div>
        </div>
    );
}

function Button(props) {

    function changeBar() {
        let progDiv = document.getElementById(props.progress);
        let number = progDiv.getElementsByClassName("percent")[0];
        number.innerHTML = props.value;
        console.log(number);
    }

    return (
        <button onClick={changeBar}>
            {props.value}
        </button>
    );
}

class SelectBox extends React.Component {

    selectBar(e) {
        let idProgress = e.target.value;
        const progressEl = document.getElementsByClass("progressBar");
        console.log(idProgress);

        for (let i = 0; i <= progressEl.lenght; i++) {
            document.getElementsByClassName("progressBar").classList.remove("selected");
        }
        document.getElementById(idProgress).classList.add("selected");
    }

    render() {
        return (
            <select onChange={this.selectBar}>
                {
                    this.props.value.map((value, index) =>
                        <option key={index} value={'progress' + index}>Progress {index + 1}</option>
                    )
                }
            </select>
        );
    }
}

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            progData: []
        };
    }

    componentDidMount() {
        fetch('http://pb-api.herokuapp.com/bars')
            .then(response => { return response.json(); })
            .then((data) => { this.setState({ progData: data, loading: false }) })
            .catch((error) => { this.setState({ error }) });
    }

    render() {

        const { progData, loading, error } = this.state;

        // Log correctly
        console.log(progData.bars);

        if (error) {
            return <main><span>Something went wrong...</span></main>;
        } else if (loading) {
            return <main><h2>Please wait...</h2></main>;
        } else {
            return (
                <main>
                    <section id="progress">
                        {progData && progData.bars &&
                            progData.bars.map((value, index) =>
                                <ProgressComp key={index} id={'progress' + index} value={value + '%'} />
                            )
                        }
                    </section>
                    <section id="controls">
                        <div className="choose">
                            <span>Choose your progress bar:</span>
                            {progData && progData.bars &&
                                <SelectBox value={progData.bars} />
                            }
                        </div>
                        <div className="change">
                            <span>Change your progress bar:</span>
                            {progData && progData.buttons &&
                                progData.buttons.map((value, index) =>
                                    <Button key={index} progress="first" value={value} />
                                )
                            }
                        </div>
                    </section>
                </main>
            );
        }
    }
}

export default Main;
