import React, { Component } from 'react';

class ProgressComp extends Component {
    selectBar() {
        let objBar = { index: this.props.index, value: this.props.value };
        const progressEl = document.getElementsByClassName("progressBar");

        for (let i = 0; i < progressEl.length; i++) {
            progressEl[i].classList.remove("selected");
        }

        progressEl[objBar.index].classList.add("selected");
        this.props.action(objBar);
    }

    percentBar() {
        let percentOfLimit = Math.round(this.props.value * 100 / this.props.limit) + '%';
        return percentOfLimit;
    }

    render() {
        return (
            <div className={this.props.class} onClick={() => this.selectBar()} >
                <div className="percent">{this.percentBar()}</div>
                <div style={{ width: this.percentBar() }} className="back"></div>
            </div>
        );
    }
}

class Button extends Component {

    constructor(props) {
        super(props);
        this.state = {
            limit: props.limit,
            initValue: 0,
            value: props.value
        };
    }

    changeBar(e) {

        let number = document.querySelector(".progressBar.selected .percent");
        let back = document.querySelector(".progressBar.selected .back");

        const limit = this.state.limit;
        let initialVal = this.state.initValue; //- Initial value
        const val = this.sate.value; //- Button value

        if (val.indexOf('-') === -1) {

            console.log('Positive!');

            let initPercent = Math.round(initialVal * 100 / limit);
            let valPercent = Math.round(val * 100 / limit);
            let calcPercent = initPercent + valPercent;
            let calc = parseInt(initialVal) + parseInt(val);

            back.classList.remove("error");

            if (calcPercent > 100) {
                back.style.width = "100%";
                back.classList.add("error");
            } else {
                back.style.width = calcPercent + "%";
            }

            number.setAttribute("value", calc);
            number.innerHTML = calcPercent + "%";

        } else {

            console.log('Negative!');

            let valPos = val * -1;

            let initPercent = Math.round(initialVal * 100 / limit);
            let valPercent = Math.round(valPos * 100 / limit);
            let calcPercent = initPercent - valPercent;
            let calc = parseInt(initialVal) - parseInt(valPos);

            if (calcPercent < 0) {
                back.style.width = "0%";
                number.setAttribute("value", 0);
                number.innerHTML = "0%";
            } else {
                back.style.width = calcPercent + "%";
                number.setAttribute("value", calc);
                number.innerHTML = calcPercent + "%";

                if (calcPercent < 100) {
                    back.classList.remove("error");
                }
            }
        }

    }

    render() {
        const valueBar = this.props.value;

        return (
            <button onClick={this.changeBar}>
                {(valueBar < 0) ? valueBar + " $" : "+" + valueBar + " $"}
            </button>
        );
    }
}

class Main extends Component {

    constructor(props) {
        super(props);
        this.changeBar = this.changeBar.bind(this);
        this.state = {
            loading: true,
            barSelected: { index: 0, initVal: 0 },
            progData: []
        };
    }

    componentDidMount() {

        //- API call data get
        //- Setting first bar selected
        fetch('https://pb-api.herokuapp.com/bars')
            .then(response => { return response.json(); })
            .then((data) => {
                this.setState({
                    loading: false,
                    barSelected: { index: 0, initialVal: data.bars[0] },
                    progData: data
                })
            })
            .catch((error) => { this.setState({ error }) });

    }

    changeBar(val) {
        this.setState({
            barSelected: val
        })
    }

    render() {

        const { progData, barSelected, loading, error } = this.state;

        if (error) {
            return <main><span>Something went wrong...</span></main>;
        } else if (loading) {
            return <main><h2>Please wait...</h2></main>;
        } else {

            // Log correctly
            console.log(
                "Limit: " + progData.limit,
                "Progress bars: " + progData.bars,
                "Buttons: " + progData.buttons
            );

            console.log(
                "Bar Selected: " + barSelected.index + " " + barSelected.initVal
            );

            return (
                <main>
                    <section id="progressBox">
                        <span>Choose your progress bar:</span>
                        <div className="choose">
                            {progData && progData.bars && (
                                progData.bars.map((value, index) =>
                                    <ProgressComp
                                        key={index}
                                        index={index}
                                        class={index === 0 ? "progressBar selected" : "progressBar"}
                                        value={value}
                                        limit={progData.limit}
                                        action={this.changeBar}
                                    />
                                )
                            )}
                        </div>
                    </section>

                    <section id="controls">
                        <div className="change">
                            <span>Add or remove money:</span>
                            {progData && progData.buttons && (
                                progData.buttons.map((value, index) =>
                                    <Button
                                        key={index}
                                        value={value}
                                        bar={barSelected}
                                        limit={progData.limit}
                                    />
                                )
                            )}
                        </div>
                    </section>

                    <section id="limit">
                        <span>Budget limit: {progData.limit}$ <i>(limit = 100%)</i></span>
                    </section>
                </main>
            );
        }
    }
}

export default Main;
