import React, { Component } from 'react';

function ProgressComp(props) {
    return (
        <div id={props.id} className="progressBar">
            <div className="percent">0%</div>
            <div className="back"></div>
        </div>
    );
}

function Button(props) {

    function changeBar() {
        let progDiv = document.getElementById(props.progress);
        let number = progDiv.getElementsByClassName("percent")[0];
        number.innerHTML = props.value;
        console.log(number);
    }

    return (
        <button onClick={changeBar}>
            {props.value}
        </button>
    );
}

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            progData: []
        };
    }

    componentDidMount() {
        fetch('http://pb-api.herokuapp.com/bars')
            .then(response => { return response.json(); })
            .then((data) => { this.setState({ progData: data }) })
            .catch((error) => { this.setState({ error }) });
    }

    render() {

        const { progData, error } = this.state;

        // Log correctly
        console.log(progData.bars);

        if (error) {
            return <main><p>Something went wrong...</p></main>;
        } else {
            return (
                <main>
                    <section id="progress">
                        {progData.bars.map(bar =>
                            <ProgressComp key={bar.objectID} id={bar.objectID} />
                        )}
                    </section>
                    <section id="controls">
                        <div className="choose">
                            <span>Choose your progress bar:</span>
                            <select>
                                <option value="first">Progress 1</option>
                                <option value="second">Progress 2</option>
                                <option value="third">Progress 3</option>
                            </select>
                        </div>
                        <div className="change">
                            <span>Change your progress bar:</span>
                            <Button progress="first" value="-25" />
                            <Button progress="first" value="-10" />
                            <Button progress="first" value="+10" />
                            <Button progress="first" value="+25" />
                        </div>
                    </section>
                </main>
            );
        }
    }
}

export default Main;
